var targetNode = $(\"#detail\")[0];

var options = {childList: true};

function callback(mutationsList, observer) {
        mutationsList.forEach(function(e){
            var x = e.addedNodes[0].children[1];
            changeAtag(x)
        });
    }
    var mutationObserver = new MutationObserver(callback);
    mutationObserver.observe(targetNode, options);
}

$(\"#history\").attr(\"onclick\",\"historyEvent()\");
function historyEvent(){

    if ($(\"#popHistory>ul a\").length > 0){

        $(\"#popHistory>ul a\").forEach(function(x){
            if(x.href.indexOf(\"javascript\") != -1){
                return;
            }
            $(x).attr(\"onclick\",\"closeDiv()\");
            changeAtag(x)
        });
    }
}

function closeDiv(){
    document.querySelector(\"#popHistory\").style.display = \"none\";
    $(\"#history\").removeClass(\"icon-history-on\");
}

function changeAtag(e){
    var temp = e.href;
    e.href=\"javascript:toMobile(\\\"\"+temp+\"\\\");\";
}

function toMobile(url){
    window.toAndroid.toNext(url);
}
